mapper.select:
select rnum, boardno, btitle, bcontent, userid, bdate, bcount, board_origin, board_re
from(
   select rownum as rnum, boardno, btitle, bcontent, userid, bdate, bcount, board_origin, board_re
      from (
         select boardno, btitle, bcontent, userid, bdate, bcount, board_origin, board_re
         from communityqnas
         order by board_origin desc, boardno asc
      )
   where rownum <=#{endRowNo}
)
where rnum >= #{startRowNo}

select boardno, btitle, bcontent, userid, bdate, bcount, board_origin, board_re
from communityqnas
where boardno=#{boardno}

select boardno, btitle, bcontent, userid, bdate, bcount, board_origin, board_re
from communityqnas
where userid=#{userid}

mapper.update:
update communityqnas set
   btitle=#{btitle},
   bcontent=#{bcontent}
   where boardno=#{boardno}
