mapper.select:
select rnum, boardno, btitle, bcontent, userid, bdate, bcount
from(
   select rownum as rnum, boardno, btitle, bcontent, userid, bdate, bcount
      from (
         select boardno, btitle, bcontent, userid, bdate, bcount
         from communityqnas
         order by boardno desc
      )
   where rownum <=#{endRowNo}
)
where rnum >= #{startRowNo}

select boardno, btitle, bcontent, userid, bdate, bcount
from communityqnas
where boardno=#{boardno}

select rnum, boardno, btitle, bcontent, userid, bdate, bcount
from(
   select rownum as rnum, boardno, btitle, bcontent, userid, bdate, bcount
      from (
         select boardno, btitle, bcontent, userid, bdate, bcount
         from communityqnas
          where userid=#{userid}
         order by boardno desc
      )

   where rownum <=#{endRowNo}
)
where rnum >= #{startRowNo}

      select rnum, boardno, btitle, bcontent, userid, bdate, bcount
      from(
         select rownum as rnum, boardno, btitle, bcontent, userid, bdate, bcount
            from (
               select boardno, btitle, bcontent, userid, bdate, bcount
               from communityqnas

					where btitle like '%' || #{keyword} || '%' or bcontent like '%' || #{keyword} || '%'


					where userid like '%' || #{keyword} || '%'

               order by boardno desc
            )

         where rownum <=#{endRowNo}
      )
      where rnum >= #{startRowNo}

mapper.update:
update communityqnas set
   btitle=#{btitle},
   bcontent=#{bcontent}
   where boardno=#{boardno}
