mapper.select:
select orderno, userid, oaddress, oreceiver, omessage, omethod, odate, ostatus, ozipcode, onumber
from orders
where orderno=#{orderNo}

select orderno, userid, oaddress, oreceiver, omessage, omethod, odate, ostatus, ozipcode, onumber
from orders
where userid=#{userId}

mapper.update:
update orders set 
ostatus='취소'
where orderno=#{orderNo}

update orders set 
oaddress=#{oAddress},
ostatus=#{oStatus}
where orderno=#{orderNo}
