mapper.select:
select orderno, userid, oaddress, oreceiver, omessage, omethod, odate, ostatus, ozipcode, onumber
from orders
where userid=#{userid}

select orderno, userid, oaddress, oreceiver, omessage, omethod, odate, ostatus, ozipcode, onumber
from orders
where orderno=#{orderno}

mapper.update:
update orders set 
ostatus='취소'
where orderno=#{orderno}

update orders set 
oaddress=#{oaddress},
ostatus=#{ostatus}
where orderno=#{orderno}
